import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import API from '../api';

const AdminDashboard = () => {
  const [ipos, setIpos] = useState([]);

  useEffect(() => {
    API.get('ipo/')
      .then((res) => setIpos(res.data))
      .catch((err) => console.error(err));
  }, []);

  const deleteIPO = (id) => {
    if (window.confirm('Are you sure you want to delete this IPO?')) {
      API.delete(`ipo/${id}/`)
        .then(() => setIpos(ipos.filter((ipo) => ipo.id !== id)))
        .catch((err) => console.error(err));
    }
  };

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-4">Admin Dashboard</h1>
      <Link
        to="/admin/edit/new"
        className="bg-blue-500 text-white px-4 py-2 rounded inline-block mb-4"
      >
        ➕ Add New IPO
      </Link>

      {ipos.map((ipo) => (
        <div
          key={ipo.id}
          className="border p-4 mb-2 flex justify-between items-center bg-white rounded shadow"
        >
          <span className="font-semibold">{ipo.company_name}</span>
          <div className="space-x-2">
            <Link
              to={`/admin/edit/${ipo.id}`}
              className="bg-yellow-400 text-white px-3 py-1 rounded"
            >
              Edit
            </Link>
            <button
              onClick={() => deleteIPO(ipo.id)}
              className="bg-red-500 text-white px-3 py-1 rounded"
            >
              Delete
            </button>
          </div>
        </div>
      ))}
    </div>
  );
};

export default AdminDashboard;
console.log("✅ AdminDashboard loaded");
