import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import API from '../api';

const IPODetail = () => {
  const { id } = useParams();
  const [ipo, setIpo] = useState(null);

  useEffect(() => {
    API.get(`ipo/${id}/`)
      .then((res) => setIpo(res.data))
      .catch((err) => console.error(err));
  }, [id]);

  if (!ipo) return <p className="p-6">Loading IPO details...</p>;

  return (
    <div className="p-6 max-w-3xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">{ipo.company_name}</h1>

      {ipo.logo && (
        <img src={`${ipo.logo}`} alt="Company Logo" />
      )}

      <div className="space-y-2">
        <p><strong>Status:</strong> {ipo.status}</p>
        <p><strong>Price Band:</strong> ₹{ipo.price_band}</p>
        <p><strong>Open:</strong> {ipo.open_date}</p>
        <p><strong>Close:</strong> {ipo.close_date}</p>
        <p><strong>Issue Size:</strong> {ipo.issue_size}</p>
        <p><strong>Issue Type:</strong> {ipo.issue_type}</p>
        <p><strong>Listing Date:</strong> {ipo.listing_date}</p>
        <p><strong>IPO Price:</strong> ₹{ipo.ipo_price}</p>
        <p><strong>Listing Price:</strong> ₹{ipo.listing_price}</p>
        <p><strong>Current Market Price:</strong> ₹{ipo.current_market_price}</p>

        {ipo.rhp_pdf && (
          <p>
            <strong>RHP:</strong>{' '}
            <a href={`${ipo.rhp_pdf}`} target="_blank" rel="noopener noreferrer">Download RHP</a>
          </p>
        )}

        {ipo.drhp_pdf && (
          <p>
            <strong>DRHP:</strong>{' '}
            <a href={`${ipo.drhp_pdf}`} target="_blank" rel="noopener noreferrer">Download DRHP</a>
          </p>
        )}
      </div>
    </div>
  );
};

export default IPODetail;
