import React, { useEffect, useState } from 'react';
import API from '../api';
import IPOCard from '../components/IPOCard';

export default function Home() {
  const [ipos, setIpos] = useState([]);

  useEffect(() => {
    API.get('ipo/')
      .then((res) => setIpos(res.data))
      .catch((err) => console.error(err));
  }, []);

  return (
    <div className="p-6">
      <h1 className="text-4xl text-purple-700 font-bold p-4">TailwindCSS is Working ðŸ’œ</h1>

      <h1 className="text-3xl font-bold mb-4">IPO Listings</h1>
      <div className="flex flex-wrap gap-4">
        {ipos.map((ipo) => (
          <IPOCard key={ipo.id} ipo={ipo} />
        ))}
        {ipos.length === 0 && (
          <p className="text-gray-500">No IPOs found. Add some in /admin</p>
        )}
      </div>
    </div>
  );
}
